import '../App.css';
import React, {Component} from 'react'
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

export default class AddTaskComponent extends Component {

    constructor(props){
        super(props)
        this.state = this.initialState;
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    initialState = {
        title:'',description:'',link:'',start:'',end:''
    }

    handleSubmit(event) {
        alert('Title: '+this.state.title+', Description: '+this.state.description+', Link: '+this.state.link+', Start Date: '+this.state.start+', Deadline: '+this.state.end);
        event.preventDefault();
    }

    handleChange(event) {
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    render() { 
        const {title,description,link,start,end} = this.state;

        return(
            <Card className='bg-dark text-white m-5'>

                <Form onSubmit={this.handleSubmit} id='taskFormId'>
                    <Card.Header>
                        <Card.Title>Add Task</Card.Title>
                    </Card.Header>
                <Card.Body className='bg-dark-blue'>

                            <Row className="mb-3">
                            <Form.Group as={Col}  controlId="formTitle"> 
                                <Form.Label>Title</Form.Label>
                                <Form.Control 
                                type="text" 
                                name='title' 
                                value={title}
                                onChange={this.handleChange} required autoComplete='off'
                                placeholder='Enter title' />
                            </Form.Group>

                            <Form.Group as={Col} controlId="formDescription">
                                <Form.Label>Description</Form.Label>
                                <Form.Control 
                                type="text" 
                                name='description' 
                                value={description}
                                onChange={this.handleChange} required autoComplete='off'
                                placeholder="What is the task?" />
                            </Form.Group>
                            </Row>

                            <Row>
                            <Col xs="6">
                            <Form.Group className="mb-3" controlId="formLink">
                                <Form.Label>Link</Form.Label>
                                <Form.Control 
                                type="text" 
                                name='link' 
                                value={link}
                                onChange={this.handleChange} autoComplete='off'
                                placeholder="Link to task" />
                            </Form.Group>
                            </Col>

                            <Form.Group as={Col} className="mb-3" controlId="formStart">
                                <Form.Label>Start Date</Form.Label>
                                <Form.Control 
                                type="date" 
                                name='start'
                                value={start}
                                onChange={this.handleChange} required autoComplete='off'/>
                            </Form.Group>

                            <Form.Group as={Col} className="mb-3" controlId="formEnd">
                                <Form.Label>Deadline</Form.Label>
                                <Form.Control 
                                type="date" 
                                name='end' 
                                value={end}
                                onChange={this.handleChange} required autoComplete='off'/>
                            </Form.Group>
                            </Row>
                        </Card.Body>
                        <Card.Footer>
                        <Button variant="success" type="submit">
                            Submit
                        </Button>
                        </Card.Footer>
                    </Form>  
            </Card>
        );
    }
}
