import React from 'react'
import TaskService from '../services/TaskService'

class TaskComponent extends React.Component {

    constructor(props){
        super(props)
        this.state = {
            tasks:[]
        }
    }

    componentDidMount() {
        TaskService.getTasks().then((response) => {
            this.setState({tasks: response.data})
        })
    }

    render() {
        return (
            <div>
                <h1 className="text-center text-white"> Tasks List</h1>
                <table className="striped bordered hover variant=dark">
                    <thead>
                        <tr>
                            <td> Task Id</td>
                            <td> Task Title</td>
                            <td> Task Description</td>
                            <td> Task Link</td>
                            <td> Task Start</td>
                            <td> Deadline</td>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            this.state.tasks.map(
                                task =>
                                <tr key = {task.id}>
                                    <td> {task.id}</td>
                                    <td> {task.title}</td>
                                    <td> {task.description}</td>
                                    <td> {task.link}</td>
                                    <td> {task.start}</td>
                                    <td> {task.end}</td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}

export default TaskComponent
