import '../App.css';
import React, {Component} from 'react'
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import { Alert } from 'react-bootstrap';

export default class AddTaskComponent extends Component {

    constructor(props){
        super(props)
        this.state = { 
            title:'',description:'',link:'',start:'',end:''
        }
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleSubmit(event) {
        alert(this.state.title);
        event.preventDefault();
    }

    handleChange(event) {
        this.setState({
            [event.target.name]:event.target.value
        });
    }

    render() {   
        return(
            <Card className='bg-dark text-white m-5'>

                <Form onSubmit={this.handleSubmit} id='taskFormId'>
                    <Card.Header>
                        <Card.Title>Add Task</Card.Title>
                    </Card.Header>
                <Card.Body className='bg-dark-blue'>

                            <Row className="mb-3">
                            <Form.Group as={Col}  controlId="formTitle"> 
                                <Form.Label>Title</Form.Label>
                                <Form.Control 
                                type="text" name='title' 
                                value={this.state.title}
                                onChange={this.handleChange} required
                                placeholder='Enter title' />
                            </Form.Group>

                            <Form.Group as={Col} controlId="description">
                                <Form.Label>Description</Form.Label>
                                <Form.Control type="text" name='description' placeholder="What is the task?" />
                            </Form.Group>
                            </Row>

                            <Row>
                            <Col xs="6">
                            <Form.Group className="mb-3" controlId="link">
                                <Form.Label>Link</Form.Label>
                                <Form.Control type="text" name='link' placeholder="Link to task" />
                            </Form.Group>
                            </Col>

                            <Form.Group as={Col} className="mb-3" controlId="start">
                                <Form.Label>Start Date</Form.Label>
                                <Form.Control type="date" name='start'/>
                            </Form.Group>

                            <Form.Group as={Col} className="mb-3" controlId="end">
                                <Form.Label>Deadline</Form.Label>
                                <Form.Control type="date" name='end' />
                            </Form.Group>
                            </Row>
                        </Card.Body>
                        <Card.Footer>
                        <Button variant="success" type="submit">
                            Submit
                        </Button>
                        </Card.Footer>
                    </Form>  
            </Card>
        );
    }
}
