import axios from 'axios'

const TASKS_REST_API_URL='http://localhost:8080/tasks';

class TaskService {

    getTasks() {
        return axios.get(TASKS_REST_API_URL);
    }

    addTask(task) {
        return axios.post(TASKS_REST_API_URL,task);
    }
}

export default new TaskService();
