import React, {Component} from 'react'
import { Card } from 'react-bootstrap'
import TaskService from '../services/TaskService'

export default class TaskComponent extends Component {

    constructor(props){
        super(props)
        this.state = {
            tasks:[]
        }
    }

    componentDidMount() {
        TaskService.getTasks().then((response) => {
            this.setState({tasks: response.data})
        })
    }

    render() {
        return (
            <div className='t-5'>
                <Card className='border border-dark text-white bg-dark t-5'>
                    <Card.Body>
                        <h1 className="text-center text-white"> Tasks List</h1>
                        <table className="table table-striped ">
                            <thead>
                                <tr>
                                    <td> Task Id</td>
                                    <td> Task Title</td>
                                    <td> Task Description</td>
                                    <td> Task Link</td>
                                    <td> Task Start</td>
                                    <td> Deadline</td>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    this.state.tasks.map(
                                        task =>
                                        <tr key = {task.id}>
                                            <td> {task.id}</td>
                                            <td> {task.title}</td>
                                            <td> {task.description}</td>
                                            <td> {task.link}</td>
                                            <td> {task.start}</td>
                                            <td> {task.end}</td>
                                        </tr>
                                    )
                                }
                            </tbody>
                        </table>
                    </Card.Body>
                </Card>
            </div>
        )
    }
}
